import { useState, useEffect } from 'react';
import { weatherService } from '../../services/api';
import {
  Container,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Alert,
} from '@mui/material';

export default function WeatherHistory() {
  const [searches, setSearches] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const response = await weatherService.getHistory();
        setSearches(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch search history');
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, []);

  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Typography>Loading...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Search History
      </Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>City</TableCell>
              <TableCell>Temperature</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Humidity</TableCell>
              <TableCell>Wind Speed</TableCell>
              <TableCell>Searched At</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {searches.map((search) => (
              <TableRow key={search.id}>
                <TableCell>{search.city}</TableCell>
                <TableCell>{search.temperature}Â°C</TableCell>
                <TableCell>{search.description}</TableCell>
                <TableCell>{search.humidity}%</TableCell>
                <TableCell>{search.windSpeed} km/h</TableCell>
                <TableCell>
                  {new Date(search.searchedAt).toLocaleString()}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}
